'use client';

import React from 'react';

const primary = '#6E0AB8';

/* ---------------------- Small helpers ---------------------- */
const Dot = () => (
    <svg
      className="inline-block align-middle shrink-0 w-2 h-2"
      viewBox="0 0 8 8"
      aria-hidden="true"
    >
      <circle cx="4" cy="4" r="3" fill="#727A90" />
    </svg>
  );

function StepItem({
    index,
    label,
    last = false,
}: {
    index: number;
    label: string;
    last?: boolean;
}) {
    return (
        <div className="relative pl-10">
            {/* number bubble */}
            <span className="absolute left-0 top-[2px] grid h-7 w-7 place-items-center rounded-full border border-[#E9EAEA] bg-white text-[12px] font-semibold text-[#6E0AB8]">
                {index}
            </span>

            {/* dotted connector (hidden on last) */}
            {!last && (
                <span className="pointer-events-none absolute left-[13px] top-8 bottom-[-18px] border-l border-dotted border-gray-300" />
            )}

            <span className="text-[14px] text-gray-700">{label}</span>
        </div>
    );
}

